import React from 'react';
import { Box, Typography, Paper } from '@mui/material';

const PredictionResult = ({ prediction }) => {
  if (!prediction) return null;

  const recommendations = Array.isArray(prediction.recommendations) 
    ? prediction.recommendations 
    : [prediction.recommendations];

  const getProbabilityClass = (confidence) => {
    if (confidence < 50) return 'probability-low-single';
    if (confidence < 75) return 'probability-medium-single';
    return 'probability-high-single';
  };

  const confidenceValue = Array.isArray(prediction.confidence) 
    ? prediction.confidence[0] 
    : prediction.confidence;

  const confidence = Math.round(confidenceValue * 100);
  const probabilityClass = getProbabilityClass(confidence);

  return (
    <Box sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 3, borderRadius: 2 }}>
        <Typography variant="h5" gutterBottom>
          Analyse-Ergebnisse
        </Typography>

        <Box sx={{ mb: 3 }}>
          <Typography variant="h6" color="primary" gutterBottom>
            Wechselwahrscheinlichkeit
          </Typography>
          <Typography variant="h3" sx={{ color: '#001B41', mb: 1 }}>
            {confidence}%
          </Typography>
          <Box sx={{
            width: '100%',
            height: '8px',
            bgcolor: '#f0f0f0',
            borderRadius: '4px',
            overflow: 'hidden',
            mb: 1
          }}>
            <Box
              sx={{
                height: '100%',
                width: `${confidence}%`,
                bgcolor: probabilityClass === 'probability-low-single' ? '#dc3545' :
                        probabilityClass === 'probability-medium-single' ? '#ffc107' : '#28a745',
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
          <Typography variant="body2" color="text.secondary">
            {prediction.status}
          </Typography>
        </Box>

        <Box sx={{ mb: 3 }}>
          <Typography variant="h6" color="primary" gutterBottom>
            Empfehlungen
          </Typography>
          <Box component="ul" sx={{ listStyle: 'none', p: 0, m: 0 }}>
            {recommendations && recommendations.length > 0 ? (
              recommendations.map((rec, index) => (
                <Box
                  component="li"
                  key={index}
                  sx={{
                    p: '12px 15px',
                    bgcolor: '#f5f5f5',
                    borderRadius: '8px',
                    mb: 1,
                    fontSize: '0.95rem',
                    color: '#666',
                    '&:last-child': { mb: 0 }
                  }}
                >
                  {rec}
                </Box>
              ))
            ) : (
              <Box
                component="li"
                sx={{
                  p: '12px 15px',
                  bgcolor: '#f5f5f5',
                  borderRadius: '8px',
                  fontSize: '0.95rem',
                  color: '#666'
                }}
              >
                Keine Empfehlungen verf√ºgbar
              </Box>
            )}
          </Box>
        </Box>

        {prediction.explanations && prediction.explanations.length > 0 && (
          <Box>
            <Typography variant="h6" color="primary" gutterBottom>
              Wichtigste Einflussfaktoren
            </Typography>
            {prediction.explanations.map((feature, index) => (
              <Box key={index} sx={{ mb: 2 }}>
                <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                  {feature.feature} ({feature.impact_percentage.toFixed(1)}%)
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {feature.description}
                </Typography>
              </Box>
            ))}
          </Box>
        )}
      </Paper>
    </Box>
  );
};

export default PredictionResult;
