import React, { useState } from 'react';
import { Box, Paper } from '@mui/material';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();
  const [hovered, setHovered] = useState(false);

  const expanded = hovered;

  const menuItems = [
    { 
      path: '/', 
      icon: '/static/images/carrer_nav.jpeg',
      text: 'Manuelle-Prognose',
      alt: 'Karriere Navigation'
    },
    { 
      path: '/linkedin', 
      icon: '/static/images/linkedin_nav.jpg',
      text: 'LinkedIn-Prognose',
      alt: 'LinkedIn Navigation'
    },
    { 
      path: '/batch', 
      icon: '/static/images/candi_batch_nav.jpg',
      text: 'Batch-Upload',
      alt: 'Batch Navigation'
    },
    { 
      path: '/candidates', 
      icon: '/static/images/candidates.png',
      text: 'Kandidaten',
      alt: 'Kandidaten Navigation'
    }
  ];

  const bottomButtons = [
    { icon: '/static/images/settings.svg', alt: 'Einstellungen' },
    { icon: '/static/images/info.svg', alt: 'Info' },
    { icon: '/static/images/plus.svg', alt: 'Hinzufügen' },
    { icon: '/static/images/arrow.svg', alt: 'Weiter' },
  ];

  return (
    <Paper
      elevation={3}
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: expanded ? '240px' : '72px',
        height: '100vh',
        bgcolor: '#fff',
        borderRadius: 0,
        boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
        transition: 'width 0.25s cubic-bezier(.4,0,.2,1)',
        zIndex: 1000,
        overflow: 'hidden',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        border: 'none',
      }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column', pt: expanded ? 3 : 2 }}>
        {/* Suchfeld */}
        <Box sx={{ px: expanded ? 2 : 1.2, mb: expanded ? 2.5 : 1.5, transition: 'all 0.2s' }}>
          <Box sx={{
            bgcolor: '#f5f5f5',
            borderRadius: 0,
            height: '38px',
            display: 'flex',
            alignItems: 'center',
            px: 1.2,
            boxShadow: 'none',
            border: '1px solid #eee',
            width: '100%',
            transition: 'all 0.2s',
          }}>
            <img src="/static/images/search.svg" alt="Suche" style={{ width: 20, height: 20, opacity: 0.7 }} />
            {expanded && (
              <input
                type="text"
                placeholder="Suchen..."
                style={{
                  border: 'none',
                  outline: 'none',
                  background: 'transparent',
                  marginLeft: 10,
                  fontSize: '1rem',
                  width: '100%',
                  color: '#222',
                }}
              />
            )}
          </Box>
        </Box>
        {/* Navigation */}
        <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column', gap: 0.5 }}>
          {menuItems.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <Box
                key={item.path}
                component={Link}
                to={item.path}
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: expanded ? 3 : 0,
                  px: expanded ? 2 : 1.2,
                  py: expanded ? 1.5 : 1.2,
                  borderRadius: 0,
                  textDecoration: 'none',
                  transition: 'background 0.2s, color 0.2s',
                  bgcolor: isActive ? '#FFEFE3' : 'transparent',
                  '&:hover': {
                    bgcolor: isActive ? '#FFEFE3' : 'rgba(255,128,0,0.13)',
                  },
                }}
              >
                <Box sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  width: 38,
                  height: 38,
                  mr: expanded ? 0.5 : 0,
                  background: isActive ? '#fff' : 'transparent',
                  borderRadius: 0,
                  transition: 'background 0.2s',
                }}>
                  <img
                    src={item.icon}
                    alt={item.alt}
                    style={{
                      width: 28,
                      height: 28,
                      objectFit: 'contain',
                      filter: isActive ? 'invert(44%) sepia(92%) saturate(749%) hue-rotate(1deg) brightness(101%) contrast(101%)' : 'none',
                    }}
                  />
                </Box>
                {expanded && (
                  <Box
                    sx={{
                      color: isActive ? '#FF8000' : '#222',
                      fontWeight: isActive ? 700 : 500,
                      fontSize: '1rem',
                      whiteSpace: 'nowrap',
                      transition: 'color 0.2s',
                    }}
                  >
                    {item.text}
                  </Box>
                )}
              </Box>
            );
          })}
        </Box>
      </Box>
      {/* Untere Buttons */}
      <Box sx={{
        display: 'flex',
        flexDirection: expanded ? 'row' : 'column',
        alignItems: 'center',
        justifyContent: 'center',
        gap: expanded ? 1.5 : 1,
        p: expanded ? 2 : 1.2,
        pb: expanded ? 2.5 : 1.5,
        transition: 'all 0.2s',
      }}>
        {bottomButtons.map((btn, i) => (
          <Box key={i} sx={{
            bgcolor: '#f5f5f5',
            borderRadius: 0,
            width: expanded ? 38 : 36,
            height: expanded ? 38 : 36,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: 'none',
            border: '1px solid #eee',
            cursor: 'pointer',
            transition: 'all 0.2s',
            '&:hover': { bgcolor: '#FF8000', borderColor: '#FF8000' },
          }}>
            <img src={btn.icon} alt={btn.alt} style={{ width: 20, height: 20, opacity: 0.8 }} />
          </Box>
        ))}
      </Box>
    </Paper>
  );
};

export default Sidebar;
