import React from 'react';
import { Box, Typography, LinearProgress } from '@mui/material';

const PredictionResult = ({ prediction }) => {
  if (!prediction) return null;

  const getProbabilityClass = (confidence) => {
    if (confidence >= 0.7) return 'high';
    if (confidence >= 0.4) return 'medium';
    return 'low';
  };

  const probabilityClass = getProbabilityClass(prediction.confidence);
  const probabilityPercentage = Math.round(prediction.confidence * 100);

  return (
    <Box
      className="prediction-content"
      sx={{
        bgcolor: 'var(--white)',
        borderRadius: '16px',
        p: '30px',
        boxShadow: 'var(--shadow)',
        width: '100%'
      }}
    >
      <Typography
        variant="h6"
        sx={{
          color: 'var(--text-color)',
          fontWeight: 600,
          mb: 3,
          fontSize: '1.3rem',
          paddingBottom: '10px',
          borderBottom: '2px solid var(--border-color)'
        }}
      >
        Prognose-Ergebnis
      </Typography>

      <Box className="probability-section" sx={{ mb: 4 }}>
        <Typography
          sx={{
            color: 'var(--text-color)',
            fontSize: '1.1rem',
            mb: 1,
            fontWeight: 500
          }}
        >
          Wahrscheinlichkeit: {probabilityPercentage}%
        </Typography>

        <Box sx={{ position: 'relative', height: '8px', borderRadius: '4px', overflow: 'hidden' }}>
          <LinearProgress
            variant="determinate"
            value={probabilityPercentage}
            sx={{
              height: '100%',
              borderRadius: '4px',
              backgroundColor: 'var(--border-color)',
              '& .MuiLinearProgress-bar': {
                backgroundColor: probabilityClass === 'high' 
                  ? 'var(--success-color)' 
                  : probabilityClass === 'medium' 
                    ? 'var(--warning-color)' 
                    : 'var(--error-color)'
              }
            }}
          />
        </Box>
      </Box>

      <Box className="recommendations-section">
        <Typography
          sx={{
            color: 'var(--text-color)',
            fontSize: '1.1rem',
            mb: 2,
            fontWeight: 500
          }}
        >
          Empfehlungen
        </Typography>

        {prediction.recommendations && prediction.recommendations.length > 0 ? (
          <Box
            component="ul"
            sx={{
              listStyle: 'none',
              p: 0,
              m: 0
            }}
          >
            {prediction.recommendations.map((recommendation, index) => (
              <Box
                component="li"
                key={index}
                sx={{
                  display: 'flex',
                  alignItems: 'flex-start',
                  py: 2,
                  borderBottom: index < prediction.recommendations.length - 1 ? '1px solid var(--border-color)' : 'none'
                }}
              >
                <Box
                  sx={{
                    width: '6px',
                    height: '6px',
                    borderRadius: '50%',
                    bgcolor: 'var(--text-color)',
                    mt: 1.2,
                    mr: 2,
                    flexShrink: 0
                  }}
                />
                <Typography
                  sx={{
                    color: 'var(--text-color)',
                    fontSize: '0.95rem',
                    lineHeight: 1.6
                  }}
                >
                  {recommendation}
                </Typography>
              </Box>
            ))}
          </Box>
        ) : (
          <Typography
            sx={{
              color: '#666',
              fontSize: '0.9rem',
              fontStyle: 'italic'
            }}
          >
            Keine Empfehlungen verf√ºgbar
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default PredictionResult;
